function to_image($type) {
    if($type == 'normal')
    echo "<td style='text-align:center;'>" . "<img src='data:image/png;base64,' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'fire')
    echo "<td style='text-align:center;'>" . "<img src='data:image/png;base64,' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'water')
    echo "<td style='text-align:center;'>" . "<img src='https://upload.wikimedia.org/wikipedia/commons/thumb/0/0b/Pokémon_Water_Type_Icon.svg/1024px-Pokémon_Water_Type_Icon.svg.png' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'electric')
    echo "<td style='text-align:center;'>" . "<img src='data:image/png;base64,' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'grass')
    echo "<td style='text-align:center;'>" . "<img src='data:image/png;base64,' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'ice')
    echo "<td style='text-align:center;'>" . "<img src='data:image/png;base64,' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'fighting')
    echo "<td style='text-align:center;'>" . "<img src='https://upload.wikimedia.org/wikipedia/commons/thumb/b/be/Pokémon_Fighting_Type_Icon.svg/1200px-Pokémon_Fighting_Type_Icon.svg.png' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'poison')
    echo "<td style='text-align:center;'>" . "<img src='data:image/png;base64,' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'ground')
    echo "<td style='text-align:center;'>" . "<img src='https://upload.wikimedia.org/wikipedia/commons/thumb/8/8d/Pokémon_Ground_Type_Icon.svg/1200px-Pokémon_Ground_Type_Icon.svg.png' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'flying')
    echo "<td style='text-align:center;'>" . "<img src='data:image/png;base64,' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'psychic')
    echo "<td style='text-align:center;'>" . "<img src='data:image/png;base64,' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'bug')
    echo "<td style='text-align:center;'>" . "<img src='https://upload.wikimedia.org/wikipedia/commons/thumb/3/3c/Pokémon_Bug_Type_Icon.svg/2048px-Pokémon_Bug_Type_Icon.svg.png' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'rock')
    echo "<td style='text-align:center;'>" . "<img src='https://upload.wikimedia.org/wikipedia/commons/thumb/b/bb/Pokémon_Rock_Type_Icon.svg/1024px-Pokémon_Rock_Type_Icon.svg.png' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'ghost')
    echo "<td style='text-align:center;'>" . "<img src='https://upload.wikimedia.org/wikipedia/commons/thumb/a/a0/Pokémon_Ghost_Type_Icon.svg/1024px-Pokémon_Ghost_Type_Icon.svg.png' width='30' height='30' border='0' />" . "</td>";
    elseif($type == 'dragon')
    echo "<td style='text-align:center;'>" . "<img src='https://upload.wikimedia.org/wikipedia/commons/thumb/a/a6/Pokémon_Dragon_Type_Icon.svg/1200px-Pokémon_Dragon_Type_Icon.svg.png' width='30' height='30' border='0' />" . "</td>";   
}
